import { useState, useEffect } from "react";
import { Form, Row, Col, Button, Alert, Modal } from "react-bootstrap";
import TimeField from 'react-simple-timefield';

export function GateModal(props) {
  let ID = parseInt(props.id);
  const [validated, setValidated] = useState(false);
  const [state, setState] = useState({});
  const [alert, setAlert] = useState(false);
  const [dummy, setDummy] = useState(false);

  useEffect(() => {
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        authID: "",
        serviceName: "getGate",
        content: {
            gateID: ID
        }
      })
    };
    
    fetch(server_URL, requestOptions)
    .then(async (response) => {
      const data = await response.json();
  
      // check for error response
      if (!response.ok) {
        // get error message from body or default to response status
        const error = (data && data.message) || response.status;
        return Promise.reject(error);
      }
      setState(data.message);
    })
    .catch((error) => {
      this.setState({ errorMessage: error.toString() });
      console.error("There was an error!", error);
    });
  }, [dummy]);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setState((prevState) => ({
      ...prevState,
      [id]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const form = e.currentTarget;
    if (form.checkValidity() === false) {
      e.preventDefault();
      e.stopPropagation();
    }
    setValidated(true);
    if (form.checkValidity()) update();
  };

  const onShowAlert = () => {
    setAlert(true);
    window.setTimeout(() => {
      setAlert(false);
    }, 2000);
  };

  const update = () => {
    const requestOptions = {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
        authID: "",
        serviceName: "modifyGate",
        content: {
            gateID: ID,
            modifyParams: state
        }
    })
    };
    fetch(server_URL, requestOptions)
    .then(async (response) => {
        const data = await response.json();
        // check for error response
        if (data.status !== "success") {
        // get error message from body or default to response status
        const error = (data && data.message) || response.status;
        return Promise.reject(error);
        }
        onShowAlert("success");
    })
    .catch((error) => {
        onShowAlert("error");
        console.error("There was an error!", error);
    });
  };

  return (
    <div>
      {alert === null ? null:
        <Alert className="alert" variant={alert==="success"?"success":"danger"}>
          <p style={{margin:0}}>{alert==="success"?"Update Successful":"An Error Occured"}</p>
        </Alert>
      }
      <Modal show={hide} onHide={toggleModel}>
        <Modal.Header closeButton>
            <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form noValidate validated={validated} onSubmit={handleSubmit}>
                <div>
                    <Form.Group as={Row}>
                    <Form.Label column sm={4}>
                        Project ID
                    </Form.Label>
                    <Col
                        sm={4}
                        className="align-items-center d-flex justify-content-center"
                    >
                        <Form.Control type="text" placeholder={ID} readOnly />
                    </Col>
                    </Form.Group>

                    <Form.Group as={Row}>
                    <Form.Label column sm={4}>
                        Project Name
                    </Form.Label>
                    <Col
                        sm={4}
                    >
                        <Form.Control
                        required
                        placeholder="Name"
                        id="gateName"
                        name="gateName"
                        value={state.gateName}
                        onChange={handleChange}
                        />
                        <Form.Control.Feedback type="invalid">
                        Gate Name is a required field.
                        </Form.Control.Feedback>
                    </Col>
                    </Form.Group>

                    <Form.Group as={Row}>
                    <Form.Label column sm={4}>
                        Project Type
                    </Form.Label>
                    <Col
                        sm={4}
                    >
                        <Form.Control
                        custom
                        required
                        as="select"
                        id="gateType"
                        name="gateType"
                        value={state.gateType}
                        onChange={handleChange}
                        >
                        <option value={""}>--Select Type--</option>
                        <option>entry</option>
                        <option>exit</option>
                        </Form.Control>
                        <Form.Control.Feedback type="invalid">
                        Gate Type is a required field.
                        </Form.Control.Feedback>
                    </Col>
                    </Form.Group>

                    <Form.Group as={Row}>
                    <Form.Label column sm={4}>
                        Open Gate for invalid/expired season pass holders
                    </Form.Label>
                    <Col
                        sm={4}
                        className="align-items-center d-flex justify-content-center"
                    >
                        <Form.Check
                        checked={state.isOpenForInvalid}
                        type="switch"
                        id="isOpenForInvalid"
                        onChange={handleChange}
                        />
                    </Col>
                    </Form.Group>

                    <Form.Group as={Row}>
                    <Form.Label column sm={4}>
                        Open Gate for temporary vehicles
                    </Form.Label>
                    <Col
                        sm={4}
                        className="align-items-center d-flex justify-content-center"
                    >
                        <Form.Check
                        checked={state.isOpenForTemp}
                        type="switch"
                        id="isOpenForTemp"
                        onChange={handleChange}
                        />
                    </Col>
                    </Form.Group>

                    <Form.Group as={Row}>
                    <Form.Label column sm={4}>
                        MA Company
                    </Form.Label>
                    <Col
                        sm={4}
                        className="align-items-center d-flex justify-content-center"
                    >
                        <Form.Control
                        required
                        placeholder="MA Company"
                        id="MACoy"
                        name="MACoy"
                        value={state.MACoy}
                        onChange={handleChange}
                        />
                        <Form.Control.Feedback type="invalid">
                        MA Company is a required field.
                        </Form.Control.Feedback>
                    </Col>
                    </Form.Group>
                </div>
                <Form.Group as={Row}>
                <Col sm={{ span: 8, offset: 7 }}>
                    <Button type="submit">Update</Button>
                </Col>
                </Form.Group>
            </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default { GateModal };
