import React from 'react';
import PropTypes from 'prop-types';

import { alertService, alertType } from '../services/index.js';

export class Alert extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            alerts: []
        };
    }

    componentDidMount() {
        // subscribe to new alert notifications
        this.subscription = alertService.onAlert(this.props.id)
            .subscribe(alert => {
                // add alert to array
                this.setState({ alerts: [...this.state.alerts, alert] });

                // auto close alert if required
                if (alert.autoClose) {
                    setTimeout(() => this.removeAlert(alert), 3000);
                }
            });

        // clear alerts on location change
    }

    componentWillUnmount() {
        // unsubscribe & unlisten to avoid memory leaks
        this.subscription.unsubscribe();
    }

    removeAlert(alert) {
        this.setState({ alerts: this.state.alerts.filter(x => x !== alert) })
    }

    render() {
        const { alerts } = this.state;
        if (!alerts.length) return null;
        return (
            <div className="m-3">
                {alerts.map((alert, index) =>
                    <Alert key={index} variant={alert.type}>
                        {alert.message}
                    </Alert>
                )}
            </div>
        );
    }
}

export default {Alert} ;
