import {
  SidebarContent,
  ProSidebar,
  Menu,
  MenuItem,
  SubMenu
} from "react-pro-sidebar";
import "react-pro-sidebar/dist/css/styles.css";
import { House, List } from "react-bootstrap-icons";
import { Navbar, Nav, NavDropdown } from "react-bootstrap";
import { store } from "../store.js";
import { useContext, useState } from "react";
import { useHistory, Link } from "react-router-dom";

export function NavBar() {
  const storeContext = useContext(store);
  const globalState = storeContext.state;
  const [toggled, setToggled] = useState(true);
  const { dispatch } = storeContext;
  let history = useHistory();
  const logout = () => {
    dispatch({
      type: "setAuth",
      value: false
    });
    dispatch({
      type: "setUser",
      value: ""
    });
    history.push("/");
  };
  const direct = (props) => {
    let path = props.target.dataset.value;
    setToggled(true);
    history.push(path);
  };
  const toggleSideBar = () => {
    setToggled(!toggled);
  };
  return (
    <div className="h-100 fixed-top clickThrough">
      <Navbar bg="dark" variant="dark" expand="lg" className="clickAble">
        <Navbar.Brand className="d-none d-sm-block" onClick={toggleSideBar}>
          <List />
        </Navbar.Brand>
        <Navbar.Brand>Admin</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto d-sm-block d-md-none">
            <Nav.Link>
              <div data-value="/home" onClick={direct}>
                Home
              </div>
            </Nav.Link>
            <Nav.Link>
              <div data-value="/project" onClick={direct}>
                Project
              </div>
            </Nav.Link>
            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
              <NavDropdown.Item data-value="/home" onClick={direct}>
                Action
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">
                Separated link
              </NavDropdown.Item>
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
        <Navbar.Collapse className="justify-content-end">
          <Navbar.Text>
            <Nav.Link onClick={logout}>Signout</Nav.Link>
          </Navbar.Text>
        </Navbar.Collapse>
      </Navbar>
      <ProSidebar
        className="clickAble d-none d-sm-block navbar-default navbar-static-top"
        collapsed={toggled}
        collapsedWidth="0px"
      >
        <SidebarContent>
          <Menu iconShape="square">
            <MenuItem icon={<House />}>
              <div data-value="/home" onClick={direct}>
                Home
              </div>
            </MenuItem>
            <MenuItem icon={<House />}>
              <div data-value="/project" onClick={direct}>
                Project
              </div>
            </MenuItem>
            <SubMenu title="Components">
              <MenuItem>Component 1</MenuItem>
              <MenuItem>Component 2</MenuItem>
            </SubMenu>
          </Menu>
        </SidebarContent>
      </ProSidebar>
    </div>
  );
}

export default { NavBar };
